import React, { Component } from 'react'

export default class ListItem extends Component {

    nameStyle = () => {
        return{
            display: 'inline-block',
            // condition ? exprIfTrue : exprIfFalse
            color: this.props.completed ? '#f4f4' : 'black'
        }
    }

    render() {
        return (
            <li style={todoItem}>
                <input type='checkbox' onChange={this.props.onCheck.bind(this, this.props.id)}/>
                <div style={this.nameStyle()}>{this.props.name}</div>
                <button style={deleteBtn}>X</button>
                <div style={date}>{this.props.date}</div>
            </li>
        )
    }
}

const todoItem = {
    margin: '10px auto'
}

const deleteBtn = {
    float: 'right',
}

const date = {
    fontSize: '12px'
}
