//UPLOAD THIS ASSIGNMENT (PROVIDE A GLITCH LINK)
//https://github.com/BarakChamo/Mobile-Application-Development/issues/1

import React, { Component } from 'react'
import ListItem from './ListItem'
import uuid from 'uuid';

export default class List extends Component {

    //removed componentWillMount due to warning
    state = {
        items: [
            {
                id: uuid.v4(),
                name: "Milk",
                addDate: Date(Date.now()).toString(),
                completed: false,
                selected: false
            },
            {
                id: uuid.v4(),
                name: "Coffee",
                addDate: Date(Date.now()).toString(),
                completed: false,
                selected: false
            },
            {
                id: uuid.v4(),
                name: "Tea",
                addDate: Date(Date.now()).toString(),
                completed: false,
                selected: false
            },
            {
                id: uuid.v4(),
                name: "Eggs",
                addDate: Date(Date.now()).toString(),
                completed: false,
                selected: false
            }
        ],

        value: '',
        selectedItems: 0
    }

    onTextInputChange(event){
        this.setState({value: event.target.value})
    }

    onButtonClick() {
        const list = this.state.items;
        list.push({id: uuid.v4(), name: this.state.value, addDate: Date(Date.now()).toString()})
        this.setState({items: list, value: ""})
    }

    onCheck = (id) => {
        this.setState({
            items: this.state.items.map(item => {
                if (item.id === id) {
                    item.completed = !item.completed
                }
                return item
            })
        })
    }

    // The filter() method creates a new array with all elements that pass the test implemented by the provided function.
    // newArr = arr.filter((element) => condition)
    delItem = (id) => {
        this.setState({
            items: this.state.items.filter(item => item.id !== id)
        }) 
    }

    multiSelect = (id) => {
        this.setState({
            items: this.state.items.map(item => {
                if (item.id === id) {
                    item.selected = !item.selected
                }
                return item
            })
        })
    }

    removeAll = (item) => {
        this.setState({
            items: this.state.items.filter(item => item.selected === false)
        })
    }

    render() {

        const listItems = []

        for(let i = 0; i < this.state.items.length; i++) {
          listItems.push(<ListItem
             key={this.state.items[i].id} 
             name={this.state.items[i].name} 
             date={this.state.items[i].addDate} 
             completed={this.state.items[i].completed}
             selected={this.state.items[i].selected}
             id = {this.state.items[i].id}
             onCheck={this.onCheck}
             delItem={this.delItem}
             multiSelect={this.multiSelect}
             />)
        }

        return (
            <div>
                <input type="text" placeholder='New task?' value={this.state.value} onChange={this.onTextInputChange.bind(this)}/>
                <button disabled={this.state.value === ''} onClick={this.onButtonClick.bind(this)}>Add</button>
                <ul>
                    {listItems}
                </ul>
                <div>{this.state.items.filter(item => item.selected === true).length} items selected</div>
                <button onClick={this.removeAll.bind(this)}>remove them all</button>
            </div>
        )
    }
}

