import React, { Component } from 'react'
import ListItem from './ListItem'

const initialItems = [
    {
        id: 1,
        name: "Milk",
        addDate: "date"
    },
    {
        id: 2,
        name: "Coffee",
        addDate: "date"
    },
    {
        id: 3,
        name: "Tea",
        addDate: "date"
    },
    {
        id: 4,
        name: "Eggs",
        addDate: "date"
    }
]

const initialValue = "New task?"

export default class List extends Component {
    componentWillMount() {
        this.setState({items: initialItems, value: initialValue})
    }

    onTextInputChange(event){
        this.setState({value: event.target.value})
    }

    onButtonClick() {
        const list = this.state.items;
        list.push({id: 5, name: this.state.value, addDate: "date"})
        this.setState({items: list, value: ""})
    }

    render() {

        const listItems = []

        for(let i = 0; i < this.state.items.length; i++) {
          listItems.push(<ListItem key={this.state.items[i].id} item={this.state.items[i].name} date={this.state.items[i].addDate}/>)
        }

        return (
            <div>
                <input type="text" value={this.state.value} onChange={this.onTextInputChange.bind(this)}/>
                <button disabled={this.state.value === ''} onClick={this.onButtonClick.bind(this)}>Add</button>
                <ul>
                    {listItems}
                </ul>
            </div>
        )
    }
}
